import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';


const ReferenceDetail = props => {
    // Current photo index
    const [photoIndex, setPhotoIndex] = useState(0)
    // LightBox state: true/false
    const [isOpen, setIsOpen] = useState(false)

    let { project } = useParams();
    const [ currentProject, setCurrentProject] = useState({})
    const [ gallery, setGallery] = useState([])
    let urlExtend = '../images/'

    // Fetch data from CMS
    useEffect(() => {
        const url = `https://api-eu-central-1.graphcms.com/v2/ckwq5z05y2e8n01xmgomm92ka/master?query=query%20MyQuery%20%7B%0A%20%20referenciaks(where%3A%20%7Bsubtitle%3A%20%22${project}%22%7D)%20%7B%0A%20%20%20%20id%0A%20%20%20%20title%0A%20%20%20%20subtitle%0A%20%20%20%20gallery%20%7B%0A%20%20%20%20%20%20fileName%0A%20%20%20%20%20%20id%0A%20%20%20%20%7D%0A%20%20%7D%0A%7D%0A&operationName=MyQuery`       
        fetch(url)
        .then(res => res.json())
        .then(json => {
            setCurrentProject(json.data.referenciaks[0])
            setGallery(json.data.referenciaks[0].gallery)
        });
    },[project])


    // Set images
    const prevImg = () => {
        if (photoIndex === 0){
            return urlExtend+gallery[gallery.length]
        }  
        return urlExtend+gallery[(photoIndex - 1) % gallery.length].fileName
    }

    const nextImg = () => {
        if (photoIndex === gallery.length){
            return urlExtend+gallery[0].fileName
        }  
        return urlExtend+gallery[(photoIndex - 1) % gallery.length]
    }

    return (
        <section id="wip" className="py-1">
                <div className="text ml-4 mt-2">
                    <h4 className="mb-05"><strong class="slash">\</strong> Referenci√°k</h4>
                    <h2 className="mb-2" id="name" data-name={project}>{currentProject.title}</h2>
                </div>
                <div className="images grid m-4">
                {gallery.map(current => (
                    // eslint-disable-next-line jsx-a11y/anchor-is-valid
                    <a className="image" onClick={() => {setIsOpen(true); setPhotoIndex(gallery.indexOf(current))}} title={current.fileName} key={current.id}>
                        <img src={urlExtend+current.fileName} alt="" style={Border}/>
                    </a>
                ))}
                </div>
                <div>
        {isOpen && (
          <Lightbox
            mainSrc={gallery[photoIndex].url}
            nextSrc={nextImg()}
            prevSrc={prevImg()}
            enableZoom={false}
            imageTitle={gallery[photoIndex].fileName}
            onCloseRequest={() => setIsOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex((photoIndex + gallery.length - 1) % gallery.length,)
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % gallery.length)}
          />
        )}
      </div>     
    </section>
    )


}

const Border = {
        borderRadius: '5px'
}


export default ReferenceDetail;
