import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css'; // This only needs to be imported once in your app


const ReferenceDetail = props => {
    // Current photo index
    const [photoIndex, setPhotoIndex] = useState(0)
    // LightBox state: true/false
    const [isOpen, setIsOpen] = useState(false)

    let { project } = useParams();
    const [ currentProject, setCurrentProject] = useState([])
    let urlExtend = '../images/'
    const [title, settitle] = useState("")

    // Fetch data from CMS
    useEffect(() => {
        const url = `https://api-eu-central-1.graphcms.com/v2/ckwq5z05y2e8n01xmgomm92ka/master?query=query%20MyQuery%20%7B%0A%20%20referenciaks(where%3A%20%7Btitle%3A%20%22${project}%22%7D)%20%7B%0A%20%20%20%20id%0A%20%20%20%20title%0A%20%20%20%20references%20%7B%0A%20%20%20%20%20%20id%0A%20%20%20%20%20%20title%0A%20%20%20%20%20%20picture%20%7B%0A%20%20%20%20%20%20%20%20fileName%0A%20%20%20%20%20%20%7D%0A%20%20%20%20%7D%0A%20%20%7D%0A%7D%0A&operationName=MyQuery`       
        fetch(url)
        .then(res => res.json())
        .then(json => {
            title = json.data
            setCurrentProject(json.data.referenciaks[0].references)
        });
    },[project])
    console.log(currentProject)
    console.log(title)

    // Set images
    const prevImg = () => {
        if (photoIndex === 0){
            return '../images/'+currentProject[currentProject.length]
        }  
        return '../images/'+currentProject[(photoIndex - 1) % currentProject.length].picture.fileName
    }

    const nextImg = () => {
        if (photoIndex === currentProject.length){
            return '../images/'+currentProject[0].picture.fileName
        }  
        return '../images/'+currentProject[(photoIndex - 1) % currentProject.length]
    }


    return (
        <section id="wip" className="py-1">
                <div className="text ml-4 mt-2">
                    <h4 className="mb-05"><strong class="slash">\</strong> Referenci√°k</h4>
                    <h2 className="mb-2" id="name" data-name={project}>{project}</h2>
                </div>
                <div className="images grid m-4">
                {currentProject.map(current => (
                    // eslint-disable-next-line jsx-a11y/anchor-is-valid
                    <a className="image" onClick={() => {setIsOpen(true); setPhotoIndex(currentProject.indexOf(current))}} title={current.picture.fileName} key={current.id}>
                        <img src={urlExtend+current.picture.fileName} alt="" style={Border}/>
                    </a>
                ))}
                </div>
                <div>
        {isOpen && (
          <Lightbox
            mainSrc={'../images/'+currentProject[photoIndex].picture.fileName}
            nextSrc={nextImg()}
            prevSrc={prevImg()}
            enableZoom={false}
            imageTitle={currentProject[photoIndex].title}
            onCloseRequest={() => setIsOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex((photoIndex + currentProject.length - 1) % currentProject.length,)
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % currentProject.length)}
          />
        )}
      </div>     
    </section>
    )


}

const Border = {
        borderRadius: '5px'
}


export default ReferenceDetail;
