import { useEffect } from "react";
import { useLocation } from "react-router-dom";

export default function ScrollToTop() {
  const { pathname, location } = useLocation();

  useEffect(() => {
    console.log(location)
    if (pathname !== '/') {
      window.scrollTo(0, 0);
      console.log("object")
    }
  }, [pathname]);

  return null;
}
