import React from 'react'
import logo from "../images/logo.png";
import '../../style.css';
import { Link } from 'react-router-dom';
import { HashLink } from 'react-router-hash-link';


let content = 
<header class="flex">
                <div class="logo">
                    <Link to="/"><img src={logo} alt="logo"/></Link>
                </div>
                <div class="pages mr-2">
                    <Link to="/" id="current-page">Főoldal</Link>
                    <HashLink to="/#work">Kivitelezés</HashLink>
                    <HashLink to="/#prices">Árak</HashLink>
                    <HashLink to="/#planning">Tervezés</HashLink>
                    <HashLink to="/most-epul">Most épül</HashLink>
                    <Link to="/referenciak">Referenciák</Link>
                    <Link to="/elado">Eladó</Link>
                    <HashLink to="/#contact">Kapcsolat</HashLink>
                </div>
                <div class="hamburger">
                    <span class="bar"></span>
                    <span class="bar"></span>
                    <span class="bar"></span>
                </div>
            </header>;
// hamburger menu
const hamburger = document.querySelector(".hamburger");
const navMenu = document.querySelector(".pages");
hamburger.addEventListener("click", () => mobileMenu());

function mobileMenu() {
    console.log("sei")
    hamburger.classList.toggle("active");
    navMenu.classList.toggle("active");
    document.getElementsByTagName("header")[0].classList.toggle("active");
}

const navLink = document.querySelectorAll(".pages")[0];

navLink.childNodes.forEach(n => {
    if (n.nodeName !== "#text")
    {
    n.addEventListener("click", closeMenu);
}});

function closeMenu() {
    hamburger.classList.remove("active");
    navMenu.classList.remove("active");
}

const Header = props => {
    return content;
}

export default Header;
