import React, { useCallback } from 'react';
import logo from "../images/logo.png";
import '../../style.css';
import { Link } from 'react-router-dom';
import { HashLink } from 'react-router-hash-link';
import ScriptTag from 'react-script-tag';

const Header = props => {
    let pages = document.getElementsByClassName('pages')[0].childNodes;

    for (let page of pages) {
        page.onClick = handleClick();
    }

    const handleClick = useCallback(props => {


        pages.forEach(element => {
            if (element !== null) {
                if (element.id !== null) {
                    element.id = "";
                }
            }
        });

        console.log(props.target)
        props.target.id = 'current-page'


        // let currentPage = '';
        // for (let letter of window.location.href.split('').reverse()) {
        //     if (letter === '/') {
        //         break
        //     }
        //     currentPage += letter
        //     console.log(letter)
        // }

        // currentPage = currentPage.split('').reverse().join('')
        //     if (currentPage === 'most-epul') {
        //         document.getElementsByClassName('most-epul')[0].id = "current-page";
        //         }

        // console.log(currentPage)

    }, [pages])

    return (
        <header className="flex">
                <div className="logo">
                    <Link to="/"><img src={logo} alt="logo"/></Link>
                </div>
                <div className="pages mr-2">
                    <HashLink to="/#house" class="fooldal">Főoldal</HashLink>
                    <HashLink to="/#work" class="kivitelezes" >Kivitelezés</HashLink>
                    <HashLink to="/#prices">Árak</HashLink>
                    <HashLink to="/#planning">Tervezés</HashLink>
                    <HashLink to="/most-epul" class="most-epul">Most épül</HashLink>
                    <Link to="/referenciak">Referenciák</Link>
                    <Link to="/elado">Eladó</Link>
                    <HashLink to="/#contact">Kapcsolat</HashLink>
                </div>
                <div className="hamburger">
                    <span className="bar"></span>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
                <ScriptTag type="text/javascript" src="../scripts.js" />

            </header>

    )
}

export default Header;
