import React, { useEffect, useState, Component } from 'react';
import { useParams } from 'react-router-dom';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css'; // This only needs to be imported once in your app


const images = [
    '//placekitten.com/1500/500',
    '//placekitten.com/4000/3000',
    '//placekitten.com/800/1200',
    '//placekitten.com/1500/1500',
  ];



class LightboxExample extends Component {
    constructor(props) {
        super(props);


    }
}


const ReferenceDetail = props => {
    const [state, setState] = useState({
        photoIndex: 0,
        isOpen: false,
        })

    let { project } = useParams();
    const [ currentProject, setCurrentProject] = useState([])
    let urlExtend = '../images/'

    useEffect(() => {
        const url = `https://api-eu-central-1.graphcms.com/v2/ckwq5z05y2e8n01xmgomm92ka/master?query=query%20MyQuery%20%7B%0A%20%20referenciaks(where%3A%20%7Btitle%3A%20%22${project}%22%7D)%20%7B%0A%20%20%20%20id%0A%20%20%20%20title%0A%20%20%20%20references%20%7B%0A%20%20%20%20%20%20id%0A%20%20%20%20%20%20title%0A%20%20%20%20%20%20picture%20%7B%0A%20%20%20%20%20%20%20%20fileName%0A%20%20%20%20%20%20%7D%0A%20%20%20%20%7D%0A%20%20%7D%0A%7D%0A&operationName=MyQuery`       
        fetch(url)
        .then(res => res.json())
        .then(json => setCurrentProject(json.data.referenciaks[0].references));
    },[project])
    console.log(Lightbox)
    console.log(currentProject)

    const { photoIndex, isOpen } = state;

    return (
        <section id="wip" className="py-1">
                <div className="text ml-4 mt-2">
                    <h4 className="mb-05"><strong class="slash">\</strong> Referenciák</h4>
                    <h2 className="mb-2" id="name" data-name={project}>{project}</h2>
                </div>
                <div className="images grid m-4">
                {currentProject.map(current => (
                    <a className="image"  onClick={() => setState({ isOpen: true })} title={current.picture.fileName} key={current.id}>
                        <img src={urlExtend+current.picture.fileName} alt="" style={Border}/>
                        <p>{current.picture.fileName}</p>
                        <p>{current.title}</p>
                        <p>{current.id}</p>
                    </a>
                ))}
                </div>
                <div>
        <button type="button" >
          Open Lightbox
        </button>

        {isOpen && (
          <Lightbox
            mainSrc={images[photoIndex]}
            nextSrc={images[(photoIndex + 1) % images.length]}
            prevSrc={images[(photoIndex + images.length - 1) % images.length]}
            onCloseRequest={() => this.setState({ isOpen: false })}
            onMovePrevRequest={() =>
              this.setState({
                photoIndex: (photoIndex + images.length - 1) % images.length,
              })
            }
            onMoveNextRequest={() =>
              this.setState({
                photoIndex: (photoIndex + 1) % images.length,
              })
            }
          />
        )}
      </div>

        </section>
    )


}

const Border = {
        borderRadius: '5px'
}


export default ReferenceDetail;
