import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const ReferenceDetail = props => {

    let { project } = useParams();
    const [ currentProject, setCurrentProject] = useState([])

    const url = `https://api-eu-central-1.graphcms.com/v2/ckwq5z05y2e8n01xmgomm92ka/master?query=query%20MyQuery%20%7B%0A%20%20referenciaks(where%3A%20%7Btitle%3A%20%22${project}%22%7D)%20%7B%0A%20%20%20%20id%0A%20%20%20%20title%0A%20%20%20%20references%20%7B%0A%20%20%20%20%20%20id%0A%20%20%20%20%7D%0A%20%20%7D%0A%7D%0A&operationName=MyQuery`

    useEffect(() => {
        fetch(url)
        .then(res => res.json())
        .then(json => setCurrentProject(json.data.referenciaks[0].references));
    },[])

    console.log(currentProject)

    return (
        <div>
            {currentProject.map(pictures => (
                <a class="image" href='./assets/img/${fileName}-${i}.jpg' title='${fileName}-${i}' key={pictures.filename}>
                   <img src="./assets/img/${fileName}-${i}.jpg" alt="" />
                   <a>{pictures}</a>
                </a>
            ))}
        </div>
    )
}

export default ReferenceDetail;
