import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Lightbox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';
import { ReferenceContext } from '../ReferenceContext';


const ReferenceDetail = props => {

    // Current photo index
    const [photoIndex, setPhotoIndex] = useState(0);

    // LightBox state: true/false
    const [isOpen, setIsOpen] = useState(false);

    // Import ReferncesContext
    const { references } = useContext(ReferenceContext);

    // Get project name from url
    const { project } = useParams();

    // States for current porject and it's gallery
    const [ currentProject, setCurrentProject] = useState({});
    const [ gallery, setGallery] = useState([]);

    // Get current project from Context
    useEffect(() => {
        const current = references.filter(reference => reference.subtitle === project)[0]
        setCurrentProject(current)
        setGallery(current.gallery)
    },[references, project]);

    // Set images
    const prevImg = () => {
        if (photoIndex === 0){
            return gallery[(gallery.length - 1)].url;
        }  
        return gallery[(photoIndex - 1) % gallery.length].url;
    }

    const nextImg = () => {
        if (photoIndex === gallery.length){
            return gallery[0].url;
        }  
        return gallery[(photoIndex + 1)].url;
    }

    return (
        <section id="wip" className="py-1">
                <div className="text ml-4 mt-2">
                    <h4 className="mb-05"><strong class="slash">\</strong> Referenci√°k</h4>
                    <h2 className="mb-2" id="name" data-name={project}>{currentProject.title}</h2>
                </div>
                <div className="images grid m-4">
                {gallery.map(current => (
                    // eslint-disable-next-line jsx-a11y/anchor-is-valid
                    <a className="image" onClick={() => {setIsOpen(true); setPhotoIndex(gallery.indexOf(current))}} title={current.fileName} key={current.id}>
                        <img src={current.url} alt="" style={Border}/>
                    </a>
                ))}
                </div>
                <div>
        {isOpen && (
          <Lightbox
            mainSrc={gallery[photoIndex].url}
            nextSrc={nextImg()}
            prevSrc={prevImg()}
            enableZoom={false}
            imageTitle={gallery[photoIndex].fileName}
            onCloseRequest={() => setIsOpen(false)}
            onMovePrevRequest={() =>
              setPhotoIndex((photoIndex + gallery.length - 1) % gallery.length)
            }
            onMoveNextRequest={() =>
              setPhotoIndex((photoIndex + 1) % gallery.length)}
          />
        )}
      </div>     
    </section>
    );


}

const Border = {
        borderRadius: '5px'
}


export default ReferenceDetail;
