import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { ReferenceContext } from '../../ReferenceContext';

const References = props => {
    const {references} = useContext(ReferenceContext);
    console.log(references)
    return (
        <body>
            <main>
                <section id="wip" class="py-1">
                    <div class="text ml-4 mt-2">
                        <h4 class="mb-05"><strong class="slash">\</strong> Referenciák</h4>
                        <h2 class="mb-2">Elkészült munkáink</h2>
                    </div>
                    <div class="images grid m-4">
                        {references.map(reference => (
                            <Link class="image" to={"/referenciak/" + reference.subtitle} key={reference.id}>
                                <img src={reference.gallery[0].url} alt="" class="mb-05"/>
                                <h3 class="mb-1">{reference.title}</h3>
                            </Link>
                        ))}
                    </div>
                </section>
            </main>
        </body>
    )
}

export default References;
